import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, Pagination } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/pagination';
import styles from './Promotions.module.css';

const Promotions: React.FC = () => {
  const promotions = [
    {
      id: 1,
      title: 'Скидка на страхование',
      description: 'Специальное предложение на страхование грузового транспорта',
      image: '/images/promo1.jpg'
    },
    {
      id: 2,
      title: 'Бонус на SIM-карты',
      description: 'Дополнительный бонус при оформлении SIM-карты',
      image: '/images/promo2.jpg'
    },
    {
      id: 3,
      title: 'Скидка на Платон',
      description: 'Специальные условия при оплате через наш центр',
      image: '/images/promo3.jpg'
    },
    {
      id: 4,
      title: 'Карта Т-Банка',
      description: 'Бесплатное обслуживание карты в течение года',
      image: '/images/promo4.jpg'
    },
    {
      id: 5,
      title: 'Штрафы со скидкой',
      description: 'Оплата штрафов со скидкой 50%',
      image: '/images/promo5.jpg'
    }
  ];

  return (
    <section className={styles.promotions}>
      <h2 className={styles.title}>Актуальные акции</h2>
      <Swiper
        modules={[Autoplay, Pagination]}
        spaceBetween={20}
        slidesPerView={1}
        pagination={{ clickable: true }}
        autoplay={{
          delay: 4000,
          disableOnInteraction: false
        }}
        breakpoints={{
          640: {
            slidesPerView: 2,
          },
          1024: {
            slidesPerView: 3,
          }
        }}
        className={styles.swiper}
      >
        {promotions.map((promo) => (
          <SwiperSlide key={promo.id} className={styles.slide}>
            <div className={styles.card}>
              <img src={promo.image} alt={promo.title} className={styles.image} />
              <div className={styles.content}>
                <h3 className={styles.cardTitle}>{promo.title}</h3>
                <p className={styles.description}>{promo.description}</p>
              </div>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </section>
  );
};

export default Promotions;
